<!DOCTYPE HTML><html><head>    <meta charset="UTF-8">    <title>php form</title>    <style>        .error {            color: #FF0000;        }    </style></head><body><?php$nameErr = $emailErr = "";$name = $email = $sex = "";if ($_SERVER["REQUEST_METHOD"] == "POST") {    if (empty($_POST["name"])) {        $nameErr = "名字不能为空";    } else if (!preg_match("/^[a-zA-Z ]+$/", $_POST["name"])) {        $nameErr = "名字不是字母";    } else {        $name = test_input($_POST["name"]);    }    if (empty($_POST["email"])) {        $emailErr = "邮箱不能为空";    } else {        $email = test_input($_POST["email"]);    }    if (empty($_POST["sex"])) {        $sex = '';    } else {        $sex = test_input($_POST["sex"]);    }}/** *对传入的数据进行删除空格、反斜线以及过滤特殊符号处理 * @param $data 传入的数据 * @return string 返回处理后的数据 */function test_input($data){    $data = trim($data);    $data = stripslashes($data);    $data = htmlspecialchars($data);    return $data;}?><h2>数据输入</h2><form method="post" action="<?php echo htmlspecialchars($_SERVER["PHP_SELF"]); ?>">    姓名: <input type="text" name="name">    <span class="error">* <?php echo $nameErr; ?></span>    <br><br>    邮箱: <input type="text" name="email">    <span class="error">* <?php echo $emailErr; ?></span>    <br><br>    性别:    <input type="radio" name="sex" value="female">女    <input type="radio" name="sex" value="male">男    <br><br>    <input type="submit" name="submit" value="提交"></form><?phpecho "<h2>数据输出:</h2>";echo "姓名" . $name;echo "<br>";echo "邮箱" . $email;echo "<br>";echo "性别" . $sex;?></body></html>